import { useState } from 'react';
import './todo.css';

export default function todo(){
    return (
        <div id="body">
            <h1> To-Do List </h1>
            <InputFields />
            <AddCurrentGoal />
        </div>    
    )
}
function InputFields(){
    return (
        <div>
            <input id="currentInput" type="text" placeholder="Add Your Goals" value="S" />
            <button id="addButton" onClick={AddCurrentGoal}> Add </button> 
        </div>
    )
}
function AddCurrentGoal(){
    let [todoArray, setTodoArray] = useState([]);
    const currentGoal = document.getElementById('currentInput').value;
    setTodoArray(todoArray.push(currentGoal));
    console.log(todoArray);
    return <ListArea />

    function ListArea(){

        const listItems = todoArray.map((todoArray) =><li>{todoArray}</li>);
        alert("I am rendered!")
        return (
            <div id="listArea"> 
             {listItems}
            </div>
        )
    }
}




