import { useState } from 'react';
import './todo.css';

export default function todo(){
    let [todoArray,setTodoArray] = useState([])
    const InputFields = () => {
        return (
            <div>
                <input id="currentInput" type="text" placeholder="Add Your Goals"  defaultValue="Add Your Goals " />
                <button id="addButton" onClick={AddCurrentGoal}> Add </button> 
            </div>
        )
    }

    const AddCurrentGoal = () => {
        const currentGoal = document.getElementById('currentInput').value;
        setTodoArray([...todoArray,currentGoal]);
        console.log(todoArray);
    }

    const ListArea = () => {
        const listItems = todoArray.map((todoArray) =><li>{todoArray}</li>);
        alert("I am rendered!")
        return (
            <div id="listArea"> 
                {listItems}
            </div>
        )
    }

    return (
        <div id="body">
            <h1> To-Do List </h1>
            <InputFields />
            <ListArea />
        </div>    
    )
}

