// import { useState } from 'react';
import { useState } from 'react';
import './todo.css';
let todoArray = ["A","B"];
export default function todo(){
    return (
        <div id="body">
            <h1> To-Do List </h1>
            <InputFields />
            <ListArea />
        </div>    
    )
}
function InputFields(){
    return (
        <div>
            <input id="currentInput" type="text" placeholder="Add Your Goals" />
            <button id="addButton" onClick={AddCurrentGoal}> Add </button> 
        </div>
    )
}

function ListArea(){
    const [listItems, setListItem] = useState(todoArray);
    setListItem(todoArray.map((todoArray) =><li>{todoArray}</li>));
    alert("I am rendered!")
    return (
        <div id="listArea"> 
            {listItems}
        </div>
    )
}


function AddCurrentGoal(){
    const currentGoal = document.getElementById('currentInput').value;
    todoArray.push(currentGoal);
    console.log(todoArray);
    ListArea();
}

function CreateComponent(props){
    return (
        <div id="listBody">
            <input type="checkbox" />
            <h4>{props.listname}</h4>
            <button>[x]</button>
        </div>
    )
}
