// import { useState } from 'react';
import './todo.css';
let todoArray = [""];
export default function todo(){
    return (
        <div id="body">
            <h1> To-Do List </h1>
            <InputFields />
            <ListArea />
        </div>    
    )
}
function InputFields(){
    return (
        <div>
            <input id="currentInput" type="text" placeholder="Add Your Goals" />
            <button id="addButton" onClick={AddCurrentGoal}> Add </button> 
        </div>
    )
}

function ListArea(){
    return (
        <div id="listArea">
        </div>
    )
}

function CallCreateComponent(){
    for(let i of todoArray) {
        return <CreateComponent listname={i}/>
    }    
}

function AddCurrentGoal(){
    const currentGoal = document.getElementById('currentInput').value;
    todoArray.push(currentGoal);

    return (
        <CallCreateComponent listname = {currentGoal} />
    )
}

function CreateComponent(props){
    return (
        <div id="listBody">
            <input type="checkbox" />
            <h4>{props.listname}</h4>
            <button>[x]</button>
        </div>
    )
}
