import React, { useState } from 'react';
import './todo.css';

export default function Todo(){
    const [todoArray,setTodoArray] = useState([]);
    const InputFields = () => {
        return (
            <div>
                <input id="currentInput" type="text" placeholder="Add Your Goals" />
                <button id="addButton" onClick={AddCurrentGoal}> Add </button> 
            </div>
        )
    }

    const ListArea = () => {
        const listItems = todoArray.map((todoArrayItem,CreateOne) => <div key={CreateOne}> <CreateComponent Name={todoArrayItem} /> </div>);
        console.log(listItems);
        return (
            <div id="listArea"> 
                {listItems}
            </div>
        )
    }

    const AddCurrentGoal = () => {
        const currentGoal = document.getElementById('currentInput').value;
        setTodoArray([currentGoal,...todoArray]);
    }

    const CreateComponent = (props) => {
        return (
          <div id="listBody">
                <div className="listComponentCheckBox"><input type="checkbox" /></div>
                <div className="listComponentName"><h4>{props.Name}</h4></div>
                <div className="listComponentCrossButton"><button><b>X</b></button></div>
            </div>
        )
    }

    return (
        <div id="body">
            <h1> To-Do List </h1>
            <InputFields />
            <ListArea />
        </div>    
    )
}
