import './App.css';
export default function todo(){
   return(
    <div id="App" className="App">
     <header className="App-header">  
        <InputField />
        <div id="listArea"></div>
    </header>
    </div>
    )
}

function addGoalToList(){
    const currentGoal = document.getElementById('currentGoal').value;
    document.getElementById('listArea').prepend(`<br> Your Goal is : ${currentGoal} `);
}

function InputField(){
    return ( 
        <div id="inputArea">
           <input id="currentGoal" type="text" placeholder="Add Your Goals" /> 
           <button id="inputButton" onClick={addGoalToList}> + </button>
        </div>
    )
}
