import './App.css';

let listArray = [];
export default function todo(){
   return(
    <div id="App" className="App">
     <header className="App-header">  
        <InputField />
        <div id="listArea">
            <CreatelistComponet listGoal={"234"} />
        </div>
    </header>
    </div>
    )
}


function addGoalToList(){
    const currentGoal = document.getElementById('currentGoal').value;
    listArray.push(currentGoal);
    console.log(listArray);
    // <CreatelistComponet listGoal={currentGoal} />
    // document.getElementById('listArea').prepend(currentGoal);

}

function InputField(){
    return ( 
        <div id="inputArea">
           <input id="currentGoal" type="text" placeholder="Add Your Goals" /> 
           <button id="inputButton" onClick={addGoalToList}> + </button>
        </div>
    )
}

function CreatelistComponet(props){
    return(
        <div id="listComponent">
            <p>{props.listGoal}</p>
        </div>
    )
}
