import React, { useState } from 'react';
import './todo.css';

export default function Todo(){
    const [todoArray,setTodoArray] = useState([]);
    const InputFields = () => {
        return (
            <div>
                <input id="currentInput" type="text" placeholder="Add Your Goals" />
                <button id="addButton" onClick={AddCurrentGoal}> Add </button> 
            </div>
        )
    }

    const CreateOne = () => {
        let [keyCount,setkeyCount] = useState(0);
        setkeyCount(keyCount+=1);
        console.log(keyCount);
        return keyCount;
    }

    const ListArea = () => {
        const listItems = todoArray.map((todoArrayItem,CreateOne) => <div key={CreateOne}> {createComponent(todoArrayItem)} </div>);
        console.log(listItems);
        return (
            <div id="listArea"> 
                {listItems}
            </div>
        )
    }

    const AddCurrentGoal = () => {
        const currentGoal = document.getElementById('currentInput').value;
        setTodoArray([currentGoal,...todoArray]);
    }

    const createComponent = (props) => {
        return (
          <div id="listBody">
                <div><input type="checkbox" /></div>
                <div><h4>{props}</h4></div>
                <div><button>edit</button></div>
                <div><button>[x]</button></div>
            </div>
        )
    }

    return (
        <div id="body">
            <h1> To-Do List </h1>
            <InputFields />
            <ListArea />
        </div>    
    )
}
